requests.tests:
tests["Status code is 200"] = responseCode.code === 200;
tests["Logout action result present"] = responseBody.has("You have been successfully logged out.");
tests["Body contains login"] = responseBody.has("Login");
tests["Body contains loginpage title"] = responseBody.has("i-MSCP - Multi Server Control Panel / Login")

tests["Body has no errors"] = !responseBody.has("<div class=\"error\">");
var username = request.data["username"];
var re = new RegExp("\\<a href\\=\\\"user_delete.php\\?delete\\_id\\=([1-9][0-9]*)\\\" class\\=\\\"icon i\\_delete\\\" onclick=\\\"return action_delete\\(\\'" + username.replace(/([.?*+^$[\]\\(){}|-])/g, "\\$1") + "\\'\\)\\\" class\\=\\\"link\\\" title\\=\\\"Delete\\\"\\>Delete\\<\\/a\\>");
var adminid = re.exec(responseBody);
if (adminid === null || adminid.length < 2) {
    tests["User id is present"] = false;
} else {
    adminid = adminid[1];
    tests["User id is present"] = adminid;
    postman.setEnvironmentVariable("auto_adminuser", username);
    postman.setEnvironmentVariable("auto_adminaddid", adminid);
}
tests["Body has username"] = responseBody.has(username);

// Get UserID (even if they manually enter it)
var urlre = /\?delete_id=([1-9][0-9]*)|(\{\{auto_adminaddid\}\})$/;
var urlparts = urlre.exec(request.url);
if (urlparts[2] == "{{auto_adminaddid}}") {
    var userid = environment["auto_adminaddid"];
} else {
    var userid = urlparts[1];
}
tests["User id in url"] = userid;
// If previous test failed, we'll get errors below, so fail this test.
if (tests["User id in url"]) {
    var re = new RegExp("\\<a href\\=\\\"admin_edit.php\\?edit\\_id\\=" + userid.replace(/([.?*+^$[\]\\(){}|-])/g, "\\$1") + "\\\" class\\=\\\"icon i\\_user\\\"");
    tests["Edit Link for Admin Gone "] = !responseBody.match(re);
} else {
    tests["Edit Link for Admin Gone "] = false;
}
tests["Body does not have Error 400"] = !responseBody.has("Error 400");
tests["Account deleted message"] = responseBody.has("<div class=\"success\">Admin account successfully deleted.</div>");
// Clean up environment
if (tests["Account deleted message"]) {
    postman.clearEnvironmentVariable("auto_adminaddid");
    postman.clearEnvironmentVariable("auto_adminuser");
}
